declare module '*.sass' {
	const content: Record<string, string>;
	export default content;
}

declare module '*.png' {
	const content: string;
	export default content;
}

declare module '*.wav' {
	const content: string;
	export default content;
}

declare module '*.mp3' {
	const content: string;
	export default content;
}

declare module '*.svg?raw' {
	const content: string;
	export default content;
}

declare module '*.svg' {
	const content: string;
	export default content;
}
